command:
if (!canvas?.scene || !canvas.grid) {
  return;
}

const table = await fromUuid('Compendium.ker-nethalas.tables.RollTable.TDqaltuh1HYMqvKQ');
if (!table) {
  return;
}
const draw = await table.draw();

if (!draw.roll.total) {
  return;
}

const tileData = {
  texture: { src: `systems/ker-nethalas/assets/D100-Rooms/${draw.roll.total.toString().padStart(2, '0')}.webp` },
  width: 900,
  height: 900,
  x: canvas.scene.dimensions.width / 2,
  y: canvas.scene.dimensions.height / 2,
};

await TileDocument.create(tileData, { parent: canvas.scene });
